export async function normalizedHtml(
  processor: Processor,
  html: string
): Promise<string> {
  return await new Promise((resolve, reject) => {
    normalizeProcessor(processor).process(html, (err, file) => {
      if (err) {
        console.error(err);
        reject(err);
      }
      resolve(String(file));
    });
  });
}
